export declare type AllowArray<T> = T | T[];
export declare type HearFunctionCondition<T, V> = (value: V, context: T) => boolean;
export declare type HearCondition<T, V> = HearFunctionCondition<T, V> | RegExp | string | number | boolean;
export declare type HearObjectCondition<T extends Record<string, any>> = Record<string, AllowArray<HearCondition<T, any>>> & {
    [P in keyof T]?: AllowArray<HearCondition<T, T[P]>>;
};
export declare type HearConditions<T> = (AllowArray<HearCondition<T, string | undefined>> | AllowArray<HearObjectCondition<T>>);
